let tab=document.getElementById("tab"),tbh=document.getElementById("tbh"),hps=document.getElementById("hps"),gnt=document.getElementById("gnt"),hwn=["Gajah","Ikan","Harimau"];function title(a){return a.replace(/(^|\s)\S/g,function(a){return a.toUpperCase()})}function tampilkan(){for(i in tab.innerHTML="",hwn){let a=`<tr><th scope="row">${parseInt(i)+1}</th><td>${hwn[i]}</td></tr>`;tab.innerHTML+=a}}tbh.addEventListener("click",function(){let a=prompt("Masukkan nama hewan baru");null!=a&&""!=a?(a=title(a),hwn.push(a),tampilkan()):alert("Nama hewan tidak boleh kosong")}),hps.addEventListener("click",function(){let a=prompt("Masukkan nomor hewan yang ingin dihapus");a=parseInt(a),0==a||a>=hwn.length?alert(`hewan dengan nomor ${a} tidak ada`):"number"!=typeof a||isNaN(a)?alert("Nomor hewan harus berupa angka & tidak boleh kosong"):(--a,hwn.splice(a,1),tampilkan())}),gnt.addEventListener("click",function(){let a=prompt("Masukkan nomor hewan yang ingin dihapus");if(a=parseInt(a),0==a||a>=hwn.length)alert(`hewan dengan nomor ${a} tidak ada`);else if("number"!=typeof a||isNaN(a))alert("Nomor hewan harus berupa angka & tidak boleh kosong");else{let b=prompt("Masukkan nama hewan baru");null!=b&&""!=b?(b=title(b),--a,hwn.splice(a,1,b),tampilkan()):alert("Nama hewan tidak boleh kosong")}}),tampilkan();